mapper.select:
select album_price as albumPrice
from inventory
where album_id = #{albumId}

mapper.update:
UPDATE INVENTORY
SET ALBUM_PRICE = #{albumPrice}
WHERE
	ALBUM_ID = #{albumId}

mapper.insert:
INSERT INTO INVENTORY (ALBUM_ID, ALBUM_PRICE, QUANTITY)
VALUES (
    #{albumId},
    #{albumPrice},
    #{aQuantity}
)
